import {useState, useEffect} from 'react';
import axios from 'axios';


function useWikiSearch(term) {
    const [data, setData] = useState(null);
    
  useEffect(()=>{
    let card ='';
    let resd =null;
    const fetchData = async() =>{
        axios.post("/search/"+term)
        .then(res =>{
            resd = res.data   
        })
        .then((resd)=>{
            console.log(resd)
            for(let i=0; i< resd[1].length; i++){
                card  += "<div className='col-md-9'> <div className='card text-center p-2 border-box-lg'> <div className='card-body text-dark'> <h4 className='card-title'>"+resd[1][i]+"</h4> </div></div></div>";
                }
                setData(card)

        })
        .catch((err)=>{
            if(err){
                throw  err
            }
        })
    }
    fetchData();
    },[term])
    return {data};
}

export default useWikiSearch
